try {
  // Parse data produk dari pesan Telegram
  const message = $input.first().json.message.text;
  const lines = message.split('\n');
  const chatId = $input.first().json.message.chat.id;

  let productData = {
    nama_produk: '',
    harga_jual: 0,
    margin: 0,
    cost: 0,
    tanggal: new Date().toISOString().split('T')[0],
    chat_id: chatId
  };

  // Parse setiap baris dengan error handling
  lines.forEach(line => {
    try {
      if (line.includes('üì¶ Nama Produk:')) {
        productData.nama_produk = line.split('üì¶ Nama Produk:')[1].trim();
      } else if (line.includes('üí∞ Harga Jual:')) {
        const hargaStr = line.split('üí∞ Harga Jual:')[1].trim().replace(/[^0-9]/g, '');
        productData.harga_jual = parseInt(hargaStr) || 0;
      } else if (line.includes('üìä Margin:')) {
        const marginStr = line.split('üìä Margin:')[1].trim().replace(/[^0-9]/g, '');
        productData.margin = parseInt(marginStr) || 0;
      } else if (line.includes('üí∏ Cost:')) {
        const costStr = line.split('üí∏ Cost:')[1].trim().replace(/[^0-9]/g, '');
        productData.cost = parseInt(costStr) || 0;
      }
    } catch (lineError) {
      console.log('Error parsing line:', line, lineError.message);
    }
  });

  // Validasi data dengan pesan error yang lebih detail
  const errors = [];

  if (!productData.nama_produk || productData.nama_produk.trim() === '') {
    errors.push('üì¶ Nama Produk tidak boleh kosong');
  }

  if (productData.harga_jual === 0 || isNaN(productData.harga_jual)) {
    errors.push('üí∞ Harga Jual harus berupa angka dan lebih dari 0');
  }

  if (productData.margin === 0 || isNaN(productData.margin)) {
    errors.push('üìä Margin harus berupa angka dan lebih dari 0');
  }

  if (productData.cost === 0 || isNaN(productData.cost)) {
    errors.push('üí∏ Cost harus berupa angka dan lebih dari 0');
  }

  // Validasi logika bisnis
  if (productData.harga_jual > 0 && productData.cost > 0 && productData.harga_jual <= productData.cost) {
    errors.push('‚ö†Ô∏è Harga Jual harus lebih besar dari Cost');
  }

  if (productData.margin > 0 && productData.harga_jual > 0 && productData.margin >= productData.harga_jual) {
    errors.push('‚ö†Ô∏è Margin tidak boleh lebih besar atau sama dengan Harga Jual');
  }

  if (errors.length > 0) {
    throw new Error('‚ùå Data produk tidak valid:\n\n' + errors.join('\n') + '\n\nüìù Format yang benar:\nüì¶ Nama Produk: [nama produk]\nüí∞ Harga Jual: [angka]\nüìä Margin: [angka]\nüí∏ Cost: [angka]\n\nContoh:\nüì¶ Nama Produk: Laptop Gaming\nüí∞ Harga Jual: 15000000\nüìä Margin: 2000000\nüí∏ Cost: 13000000');
  }

  // Tambahkan calculated fields
  productData.profit = productData.harga_jual - productData.cost;
  productData.margin_percentage = Math.round((productData.margin / productData.harga_jual) * 100);

  return [productData];

} catch (error) {
  console.error('Error in Parse Product Data:', error);
  throw new Error(error.message || 'Terjadi kesalahan saat memproses data produk');
}

