try {
  // Baca data dari Google Sheets atau buat data kosong jika belum ada
  let salesData = [];
  let chatId;
  let hasValidData = false;

  // Cek apakah ada input dari Google Sheets atau dari trigger
  if ($input.all().length > 0) {
    const inputData = $input.all();

    // Ambil chat_id dari berbagai sumber
    for (let i = 0; i < inputData.length; i++) {
      const item = inputData[i];

      // Prioritas 1: dari message chat
      if (item.json && item.json.message && item.json.message.chat && item.json.message.chat.id) {
        chatId = item.json.message.chat.id;
        break;
      }
      // Prioritas 2: dari chat_id langsung
      else if (item.json && item.json.chat_id) {
        chatId = item.json.chat_id;
        break;
      }
    }

    // Proses data dari Google Sheets
    if (inputData.length > 0 && inputData[0].json) {
      // Jika input adalah array data dari Google Sheets
      if (Array.isArray(inputData) && inputData.length > 0) {
        salesData = inputData
          .filter(item => item.json && typeof item.json === 'object')
          .map(item => {
            const data = item.json;
            return {
              tanggal: data.tanggal || data.Tanggal || '',
              nama_produk: data.nama_produk || data['Nama Produk'] || data.product || '',
              harga_jual: parseInt(String(data.harga_jual || data['Harga Jual'] || data.price || 0).replace(/[^0-9]/g, '')) || 0,
              margin: parseInt(String(data.margin || data.Margin || 0).replace(/[^0-9]/g, '')) || 0,
              cost: parseInt(String(data.cost || data.Cost || 0).replace(/[^0-9]/g, '')) || 0
            };
          })
          .filter(item => item.nama_produk && item.nama_produk.trim() !== '' && item.harga_jual > 0);

        hasValidData = salesData.length > 0;
      }
    }
  }

  // Validasi chat_id - gunakan environment variable jika tidak ada
  if (!chatId) {
    try {
      chatId = $env.ADMIN_CHAT_ID;
      if (!chatId) {
        throw new Error('ADMIN_CHAT_ID tidak ditemukan di environment variables');
      }
    } catch (error) {
      console.log('Error accessing ADMIN_CHAT_ID:', error.message);
      // Fallback chat ID untuk testing (ganti dengan chat ID admin yang valid)
      chatId = '123456789'; // Ganti dengan chat ID admin yang sebenarnya
    }
  }

  // Jika tidak ada data yang valid
  if (!hasValidData || salesData.length === 0) {
    return [{
      json: {
        hasData: false,
        message: 'üìä Belum ada data penjualan di Google Sheets.\n\nüîπ Silakan tambahkan data produk terlebih dahulu dengan format:\n\nüì¶ Nama Produk: [nama produk]\nüí∞ Harga Jual: [harga]\nüìä Margin: [margin]\nüí∏ Cost: [cost]\n\nContoh:\nüì¶ Nama Produk: Laptop Gaming\nüí∞ Harga Jual: 15000000\nüìä Margin: 2000000\nüí∏ Cost: 13000000',
        chat_id: chatId
      }
    }];
  }

  // Hitung total dengan validasi
  const totalPenjualan = salesData.reduce((sum, item) => sum + (item.harga_jual || 0), 0);
  const totalMargin = salesData.reduce((sum, item) => sum + (item.margin || 0), 0);
  const totalCost = salesData.reduce((sum, item) => sum + (item.cost || 0), 0);
  const totalProfit = totalPenjualan - totalCost;

  // Siapkan data untuk Excel dengan format yang lebih baik
  const excelData = [
    ['LAPORAN KEUANGAN PENJUALAN'],
    ['Tanggal Laporan:', new Date().toLocaleDateString('id-ID', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    })],
    ['Waktu Generate:', new Date().toLocaleTimeString('id-ID')],
    [''],
    ['No', 'Tanggal', 'Nama Produk', 'Harga Jual (Rp)', 'Margin (Rp)', 'Cost (Rp)', 'Profit (Rp)'],
    ...salesData.map((item, index) => [
      index + 1,
      item.tanggal || 'N/A',
      item.nama_produk || 'N/A',
      item.harga_jual || 0,
      item.margin || 0,
      item.cost || 0,
      (item.harga_jual || 0) - (item.cost || 0)
    ]),
    [''],
    ['RINGKASAN KEUANGAN'],
    ['Total Penjualan (Rp):', totalPenjualan],
    ['Total Margin (Rp):', totalMargin],
    ['Total Cost (Rp):', totalCost],
    ['Total Profit (Rp):', totalProfit],
    ['Jumlah Produk Terjual:', salesData.length],
    ['Rata-rata Harga per Produk (Rp):', Math.round(totalPenjualan / salesData.length) || 0],
    ['Margin Rata-rata (%):', Math.round((totalMargin / totalPenjualan) * 100) || 0]
  ];

  return [{
    json: {
      hasData: true,
      excelData,
      totalPenjualan,
      totalMargin,
      totalCost,
      totalProfit,
      jumlahProduk: salesData.length,
      rataRataHarga: Math.round(totalPenjualan / salesData.length) || 0,
      marginPersentase: Math.round((totalMargin / totalPenjualan) * 100) || 0,
      chat_id: chatId,
      salesData: salesData
    }
  }];

} catch (error) {
  console.error('Error in Generate Report:', error);

  // Fallback chat ID
  let fallbackChatId;
  try {
    fallbackChatId = $env.ADMIN_CHAT_ID || '123456789';
  } catch (e) {
    fallbackChatId = '123456789';
  }

  return [{
    json: {
      hasData: false,
      message: '‚ùå Terjadi kesalahan saat memproses laporan:\n\n' + error.message + '\n\nüîß Silakan coba lagi atau hubungi administrator.',
      chat_id: fallbackChatId,
      error: error.message
    }
  }];
}

